var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var userSchema = mongoose.Schema({
	zoneID : String,
	vertices : [{
		latitude	: String,
		longitude	: String
	}],
	hazard : [{
		HazardId : Number,
		HazardLat : String,
		HazardLong : String,
		HazardType : String
	}]
});

mongoose.connect('mongodb://localhost:27017/testdb1');
module.exports = mongoose.model('zones', userSchema);