var mongoose = require('mongoose');
var zone = require('scripts/models');

exports.addzone = function(data,callback) {

	console.log(data.zoneID);

	var newzone = new zone({
		zoneID: data.zoneID,
		vertices: data.vertices,
		hazard: data.hazard
	});

	newzone.save(function(err) {
		callback({'reponse':'data entered'});
	} );

	//callback({'callback ok'});

}